import{_ as i,c as n,o as a,aB as t}from"./chunks/framework.uEqVN41L.js";const p=JSON.parse('{"title":"About MtF.Report","description":"","frontmatter":{"title":"About MtF.Report","index":true,"icon":"creative","category":["Main"],"wordCount":378},"headers":[],"relativePath":"en/index.md","filePath":"en/index.md","lastUpdated":1749984299000}'),o={name:"en/index.md"};function r(s,e,l,d,u,c){return a(),n("div",null,e[0]||(e[0]=[t('<h2 id="advocacy-and-support-for-transgender-women-mtf-in-china" tabindex="-1">Advocacy and Support for Transgender Women (MtF) in China <a class="header-anchor" href="#advocacy-and-support-for-transgender-women-mtf-in-china" aria-label="Permalink to &quot;Advocacy and Support for Transgender Women (MtF) in China&quot;">​</a></h2><p>MtF.Report is a grassroots platform dedicated to researching and supporting transgender women (MtF) youth in China. Through in-depth studies and authentic stories, we strive to break informational barriers, helping the world understand their struggles and aiding them toward safer and more dignified lives.</p><h3 id="core-features" tabindex="-1">Core Features <a class="header-anchor" href="#core-features" aria-label="Permalink to &quot;Core Features&quot;">​</a></h3><ul><li><p><strong>Research Reports</strong> Compiling oral interviews and academic resources, we regularly publish the <em>Report on the Living Conditions of Transgender Women in China</em>, highlighting deep-rooted issues such as family violence, medical discrimination, and social exclusion.</p></li><li><p><strong>Donation Channel</strong> Transparent and secure donation links ensuring each contribution directly supports:</p><ol><li>Basic living essentials (food, shelter, daily necessities)</li><li>Safe evacuation and emergency shelters</li><li>Medication purchases and psychological counseling</li><li>Volunteer stipends and training</li></ol></li></ul><h2 id="origin-and-background" tabindex="-1">Origin and Background <a class="header-anchor" href="#origin-and-background" aria-label="Permalink to &quot;Origin and Background&quot;">​</a></h2><p>MtF.Report was officially launched in June 2025 by three students. During an online discussion, the founders became deeply aware of the severe oppression and discrimination faced by young transgender females (MtF) in China within their families, schools, and healthcare systems. Driven by empathy and compassion, they decided to use their efforts to advocate for and support this marginalized group.</p><h2 id="initial-motivation-and-vision" tabindex="-1">Initial Motivation and Vision <a class="header-anchor" href="#initial-motivation-and-vision" aria-label="Permalink to &quot;Initial Motivation and Vision&quot;">​</a></h2><ul><li><strong>Motivation</strong>: To challenge societal prejudices and misunderstandings about transgender communities by revealing their true living conditions through authentic research reports.</li><li><strong>Vision</strong>: Regularly publishing oral interviews and data-driven reports to inform the public, policymakers, and potential supporters about the necessary assistance required; building a transparent donation platform to directly allocate funds to improve their lives and ensure their safety.</li></ul><h2 id="mission-and-vision" tabindex="-1">Mission and Vision <a class="header-anchor" href="#mission-and-vision" aria-label="Permalink to &quot;Mission and Vision&quot;">​</a></h2><ul><li><p><strong>Mission</strong>:</p><ol><li>Collect and disseminate authentic data and stories of transgender female youths in China.</li><li>Raise and distribute aid funds to provide basic living essentials, medical care, and psychological support.</li><li>Establish an advocacy network for transgender rights to promote societal inclusion and policy improvements.</li></ol></li><li><p><strong>Vision</strong>:</p><ul><li>Gradually eliminate societal prejudice through ongoing research and education, ensuring equal respect and protection for transgender youths across all sectors.</li></ul></li></ul><h2 id="team-introduction" tabindex="-1">Team Introduction <a class="header-anchor" href="#team-introduction" aria-label="Permalink to &quot;Team Introduction&quot;">​</a></h2><ul><li><p><strong>Core Founders (all online aliases)</strong></p><ul><li>Yuho・H</li><li>CodingXiaoheng</li><li>Chiyu</li><li>WhiteRabbitSpirit</li></ul></li><li><p><strong>Volunteer Network &amp; Partners</strong></p><ul><li>Currently under development. We warmly welcome passionate individuals and professional organizations to join us!</li></ul></li></ul><h2 id="contact-us" tabindex="-1">Contact Us <a class="header-anchor" href="#contact-us" aria-label="Permalink to &quot;Contact Us&quot;">​</a></h2><ul><li><strong>Email</strong>: <a href="mailto:mtf.report@proton.me" target="_blank" rel="noreferrer">mtf.report@proton.me</a></li></ul><blockquote><p>Thank you for your attention and support. Let’s work together to build a better future for transgender youth.</p></blockquote>',15)]))}const g=i(o,[["render",r]]);export{p as __pageData,g as default};
