import{_ as t,c as a,o as i,aB as e}from"./chunks/framework.uEqVN41L.js";const p=JSON.parse('{"title":"MtF.Report:让世界看见她们","description":"","frontmatter":{"title":"MtF.Report:让世界看见她们","index":true,"icon":"creative","category":["主页"],"wordCount":737},"headers":[],"relativePath":"zh-cn/index.md","filePath":"zh-cn/index.md","lastUpdated":1749984299000}'),o={name:"zh-cn/index.md"};function r(n,l,s,d,h,u){return i(),a("div",null,l[0]||(l[0]=[e('<h2 id="为中国跨性别女性-mtf-发声与援助" tabindex="-1">为中国跨性别女性（MtF）发声与援助 <a class="header-anchor" href="#为中国跨性别女性-mtf-发声与援助" aria-label="Permalink to &quot;为中国跨性别女性（MtF）发声与援助&quot;">​</a></h2><p>MtF.Report 是一个致力于研究与支持中国青少年跨性别女性（MtF）的小型公益平台。我们通过深入的调研和真实案例，打破信息壁垒，让世界了解她们的处境，帮助她们走出困境，迈向更安全、更有尊严的生活。</p><h3 id="核心功能" tabindex="-1">核心功能 <a class="header-anchor" href="#核心功能" aria-label="Permalink to &quot;核心功能&quot;">​</a></h3><ul><li><p><strong>研究报告</strong> 系统整理口头访谈与文献资料，定期发布《中国跨性别女性生存现状报告》，揭示家庭暴力、医疗歧视、社会排斥等深层问题。</p></li><li><p><strong>捐助通道</strong> 透明、安全的捐助链接，将每一笔善款切实用于：</p><ol><li>基本生活支出（食宿、日常用品）</li><li>安全撤离与紧急避护</li><li>药品采购与心理咨询</li><li>志愿者补贴与培训</li></ol></li></ul><h2 id="起源与背景" tabindex="-1">起源与背景 <a class="header-anchor" href="#起源与背景" aria-label="Permalink to &quot;起源与背景&quot;">​</a></h2><p>MtF.Report 于 2025 年 6 月正式发起，项目由三位学生共同创建。三位发起者在一次线上讨论中深切了解到国内青少年跨性别女性（MtF）在家庭、学校和医疗体系中所面临的多重压迫与歧视，深感惋惜与同情，决定用自己的力量为她们发声与援助。</p><h2 id="最初动机与设想" tabindex="-1">最初动机与设想 <a class="header-anchor" href="#最初动机与设想" aria-label="Permalink to &quot;最初动机与设想&quot;">​</a></h2><ul><li><strong>动机</strong>：打破社会对跨性别群体的偏见与误解，用真实的调研报告揭示她们的生存现状；</li><li><strong>设想</strong>：通过定期发布口头访谈与数据报告，向社会公众、政策制定者和潜在援助方展示她们所需的支持；同时搭建透明的捐助平台，将每一笔善款直接用于改善她们的生活与安全。</li></ul><h2 id="使命与愿景" tabindex="-1">使命与愿景 <a class="header-anchor" href="#使命与愿景" aria-label="Permalink to &quot;使命与愿景&quot;">​</a></h2><ul><li><strong>使命</strong>： <ol><li>收集并发布中国青少年跨性别女性的真实生存数据和故事；</li><li>筹集并分发援助资金，为受困者提供基本生活、医疗和心理支持；</li><li>建立跨性别权益倡导网络，推动社会包容与政策改善。</li></ol></li><li><strong>愿景</strong>： <ul><li>通过持续的调查与报告，逐步消除社会偏见，让跨性别青少年在各个领域都能获得平等尊重与保护。</li></ul></li></ul><h2 id="团队介绍" tabindex="-1">团队介绍 <a class="header-anchor" href="#团队介绍" aria-label="Permalink to &quot;团队介绍&quot;">​</a></h2><ul><li><strong>核心发起人（均为网络名称）</strong><ul><li>祐穂・H</li><li>CodingXiaoheng</li><li>池鱼</li><li>白兔子成精</li></ul></li><li><strong>志愿者网络 &amp; 合作伙伴</strong><ul><li>目前正在搭建中，欢迎有志之士和专业机构加入！</li></ul></li></ul><h2 id="联系我们" tabindex="-1">联系我们 <a class="header-anchor" href="#联系我们" aria-label="Permalink to &quot;联系我们&quot;">​</a></h2><ul><li><strong>邮箱</strong>：mtf.report@proton.me</li></ul><blockquote><p>感谢每一份关注与支持，让我们一起为跨性别青少年的未来贡献力量。</p></blockquote>',15)]))}const g=t(o,[["render",r]]);export{p as __pageData,g as default};
